import com.eviware.soapui.support.XmlHolder
def xmlresponse=messageExchange.response.responseContent
def parsexmlresponse=new XmlHolder (xmlresponse)
parsexmlresponse.namespaces["ns"] = 'http://tempuri.org/'
log.info parsexmlresponse.getNodeValue("//ns:AddResponse/ns:AddResult")
def Expected= parsexmlresponse.getNodeValue("//ns:AddResponse/ns:AddResult")
// Set property value-- gets value from the resoponse and out it in the property value(ExpectedAddOutput)
context.getTestCase().setPropertyValue("ExpectedAddOutput", Expected)
def Actual= context.getTestCase().getPropertyValue("ExpectedAddOutput")

assert Actual==Expected

