con:config.script:
// to run add testcase automatically 

import com.eviware.soapui.support.XmlHolder
import com.eviware.soapui.impl.wsdl.testcase.WsdlTestRunContext
def Addrequest=testRunner.testCase.testSuite.testCases["Add TestCase"].testSteps["Add"].getPropertyValue("Request")
def AddintAVar=testRunner.testCase.testSuite.testCases["Add TestCase"].getPropertyValue("AddintA")
def AddintBVar=testRunner.testCase.testSuite.testCases["Add TestCase"].getPropertyValue("AddintB")
 def xmlAdd = new XmlHolder(Addrequest)

xmlAdd.setNodeValue("//tem:Add/tem:intA",AddintAVar)
xmlAdd.setNodeValue("//tem:Add/tem:intB",AddintBVar)
def newAddXml=xmlAdd.getXml()
testRunner.testCase.testSuite.testCases["Add TestCase"].testSteps["Add"].setPropertyValue("Request", newAddXml)
log.info xmlAdd.getXml()

//call the service Add
def AddTestStep=testRunner.testCase.testSuite.testCases["Add TestCase"].testSteps["Add"]
def ContextAdd=new WsdlTestRunContext(AddTestStep)
AddTestStep.run(testRunner,ContextAdd)

//call the service Subtract
def SubtractTestStep=testRunner.testCase.testSuite.testCases["Subtract TestCase"].testSteps["Subtract"]
def ContextSubtract=new WsdlTestRunContext(SubtractTestStep)
SubtractTestStep.run(testRunner,ContextSubtract)

